INSERT INTO `products` (`id`, `title`, `description`, `author`, `price`, `image`, `cat`, `link`) VALUES
(1, 'Новый англо-русский, русско-английский словарь. 100000 слов', 'Словарь содержит около 100 000 слов и словосочетаний, которые широко охватывают лексику современного английского языка. Широко представлены слова современной актуальной общественно-политической, разговорной, специальной лексики. Кроме того приводятся устойчивые обороты речи, идиоматические выражения, пословицы и поговорки. Предназначен для углубленного изучения английского языка.', 'Карантиров Сергей', '90.70', '9785903036141.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=1'),
(2, 'Французский язык. Компактный справочник грамматики', 'Вашему вниманию предлагается компактный справочник грамматики по французскому языку. Уникальность издания заключается в том, что весь материал справочника разбит на тематические блоки.', '', '26.30', '9785386030711.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=2'),
(3, 'Русско-арабский разговорник', 'Русско-арабский разговорник предназначен прежде всего для граждан, которые выезжают за границу, а также для школьников, студентов и всех, изучающих арабский. Разговорник включает наиболее употребительные слова и словосочетания, а также полезную для туристов информацию страноведческого характера.', 'Олег Таланов', '19.05', '9789664981658.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=3'),
(4, 'Хорошо говорю на хорошем английском. Грамматика', 'Этот учебник представляет собой уроки-лекции о наиболее употребительных английских временах и предназначен, главным образом, для тех, кто изучает язык самостоятельно, для репетиторов и учителей, желающих повысить знания своих учеников. Несмотря на то, что речь идет о грамматике, учебник строится не на сухих правилах. Множество разнообразных примеров и больших высказываний основано па живой разговорной речи. Причем каждый английский текст или диалог сопровождаются параллельным русским переводом. Манера изложения материалов необычна. Автор говорит о каждом времени с уважением и даже любовью, подкрепляя свой рассказ образным сравнением.', 'Валентина Фрольонок', '97.20', '2000072600465.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=4'),
(5, 'Самоучитель. Китайский язык для начинающих', 'Языком XX века был, безусловно, английский. Языком XXI века станет китайский. Знание китайского языка позволит вам вести переговоры, изучать культуру и просто путешествовать по стране, где зародилась одна из величайших цивилизаций. Учебник, который вы держите в руках, состоит из уроков, где подробнейшим образом изложены основы китайского языка, детально описаны многие его нюансы и тонкости. Фонетический материал дает возможность проработать самые сложные аспекты произношения, в иероглифических разделах вы найдете подробную этимологию графем, познакомитесь со всеми вариантами их написания. В основе новой лексики и грамматики лежит материал, уже изученный на предыдущих уроках, что значительно упрощает его усвоение. Каждый урок снабжен заданиями, упражнениями и прописями, позволяющими закрепить изучаемый материал, задействовав различные аспекты памяти. Кроме того, к книге прилагается компакт-диск, озвученный носителем языка. Он поможет вам овладеть китайским произношением, столь непривычным нашему уху, ведь, как известно, в китайском языке от интонации зачастую зависит смысл слова или целой фразы. Автор учебника Милена Карлова много лет дает частные уроки китайского. Свои знания, основанные на серьезных научных трудах и долгой практике, она облекла в легкую и удобную для обучения форму, благодаря чему китайский язык теперь доступен каждому!', 'Милена Карлова', '93.20', '9785496001755.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=5'),
(6, 'Испанский за 6 недель', 'Простота и практическая направленность курса позволяет достичь быстрого прогресса, а использование игровых ситуаций делает процесс обучения живым и интересным.', 'Элизабет Смит', '57.10', '9789664982488.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=6'),
(7, 'Русско-румынский разговорник', 'Данное издание представляет собой карманный русско-румынский разговорник.', 'Олег Таланов', '19.59', '9785992504460.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=7'),
(8, 'История человечества. С древних времен до наших дней', 'В одном томе представлены главные вехи истории человечества. В хронологических таблицах кратко изложены события, происходившие в разные эпохи на разных континентах. Более подробную информацию читатель почерпнет из развернутых статей по важнейшим темам. В конце книги помещены краткий исторический словарь и подробный указатель.', '', '99.99', '9785389052574.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=8'),
(9, 'Учебник быстрого чтения', 'Книга предлагает эффективную программу, позволяющую значительно активизировать работу и взаимодействие глаз и головного мозга в процессе чтения литературы. Для широкого круга читателей.', 'Тони Бьюзен', '51.60', '9789851513051.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=9'),
(10, 'Удивительные события, изменившие историю человечества', 'Высоко в горах падение одного-единственного камня может вызвать сход сокрушительной лавины. Так и в истории человечества события, порой кажущиеся незначительными, влекут за собой войны и падение государств, выдающиеся открытия и судьбоносные изменения в жизни отдельно взятых людей. Маленькие причины больших событий — это ключ к пониманию загадок мировой истории. Автор этой замечательной книги остроумно анализирует знаменитые исторические казусы и курьезы с древности до наших дней. Ему первому удалось найти ответы на вечные вопросы истории «Кто?», «Как?», «Почему?» и, наконец, «Кому это выгодно?». Адресовано широкой читательской аудитории.', 'Мазуркевич Сергей Александрови', '50.20', '9789664818961.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=10'),
(11, 'Словарь трудностей русского языка', 'В настоящем словаре собрано около 20 ООО слов, представляющих трудности различного характера. Читатель получит справку о написании, произношении, формообразовании слова, узнает грамматическую и стилистическую характеристику слова, возможную сочетаемость, управление, а также правильность употребления слова.', 'Розенталь Дитмар', '87.10', '9785811248520.jpg', 'educational_literature', 'http://localhost/bookseller/index.php?view=product&id=11'),
(12, 'Введение в психоанализ', 'Зигмунд Фрейд, австрийский ученый, психиатр, невролог, известен во всем мире как создатель современной психологии. Он первым исследовал природу сексуальности, напрямую связав ее с моделями поведения человека и развитием характера, пытался разгадать тайну сновидений, а его теория психоанализа оказала огромное влияние на медицину, антропологию, философию и искусство XX века. Среди самых известных его трудов "Толкование сновидений", "Очерки по теории сексуальности, "Тотем и табу", "По ту сторону принципа удовольствия" и другие. "Введение в психоанализ" составили лекции, прочитанные доктором Фрейдом в 1915-1917 и 1930-х годах. В этой работе он исследует бессознательные психологические процессы. Ученый обнаружил, что травматические события в жизни человека, сильные переживания, неисполнившиеся и подавленные желания не исчезают из психики, а уходят в сферу бессознательного, продолжая воздействовать на поведение. Это открытие позволило разрешить конфликт между тем, что говорит сознание, и неосознанными побуждениями, а говоря другими словами, найти, по сути, лекарство для души.', 'Зигмунд Фрейд', '29.90', '9785905799471.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=12'),
(13, 'Очерки по психологии сексуальности', 'Зигмунд Фрейд - знаменитый австрийский психолог и психиатр, основоположник психоанализа, ученый, чьи открытия обогатили многие области современных знаний. Его работы "Толкование сновидений", "Введение в психоанализ", "По ту сторону принципа наслаждения", "Тотем и табу", "Я и Оно" вызывают интерес не только у специалистов-психологов, но и у самого широкого круга читателей. Настоящее издание включает "Очерки по психологии сексуальности" Фрейда: "К теории полового влечения", "О нарциссизме", "Об унижении любовной жизни" - и созданные в дополнение к ним очерки "Табу девственности", "Инфантильная генитальная организация". В очерках, значение которых для научной и интеллектуальной жизни нашей эпохи трудно переоценить, Фрейд разрабатывает психологию сексуальности, формулируя революционные и вскоре ставшие классическими положения. Очерки отражают представления Фрейда о природе сексуальных отклонений и извращений, о явлениях бисексуальности, фетишизма, нарциссизма, детской сексуальности и культурно-социального табу девственности.', 'Зигмунд Фрейд', '22.20', '9785389055155.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=13'),
(14, 'Золотые правила брака: универсальные советы на все случаи жизни', 'Знаменитый американский психолог, специализирующаяся в вопросах брака, Харриет Лернер в своей новой книге знакомит нас с сотней правил, которые охватывают все "горячие точки" долгосрочных отношений. "Золотые правила брака" - это уникальная сокровищница живых, практических советов, которые помогут вам ориентироваться в проблемах отношений вашей пары с ясностью, мужеством и радостью. Даже если только один из партнеров следует хотя бы десяти правилам на свой выбор, это будет способствовать положительным переменам, развитию и укреплению вашего союза. Все, что для этого потребуется, - искреннее желание улучшить отношения и готовность к практике.', 'Харриет Лернер', '35.20', '9785399004396.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=14'),
(15, 'Сила концентрации', 'Концентрация - это сосредоточение внимания только на позитивных, созидательных мыслях. Человек, владеющий этим искусством, достигает максимального успеха в любом деле. В своей классической книге Уильям Аткинсон дает простые, но эффективные упражнения, которые помогут вам быстро выработать навыки концентрации и двигаться к новым успехам в бизнесе и повседневной жизни.', 'Уильям Аткинсон', '32.60', '9785399004297.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=15'),
(16, 'Очерки о традиции и метафизике', 'Книга Рене Генона, одного из наиболее влиятельных европейских философов эзотеризма, представляет собой сборник статей, расположенных по разделам, соответствующим структуре традиционного знания. В них представлена вся шкала интересов Генона, который усматривал скрытый смысл и единство содержания как в высокой метафизике или религиозной мистике, так и в истории алхимии или карт Таро.', 'Рене Генон', '24.70', '9785998506529.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=16'),
(17, 'Я и Оно. По ту сторону принципа удовольствия.', '"Я и Оно". Сознание - и подсознание. Сознание выставляет преграды - подсознание предпочитает их не замечать. И тогда сознание становится всего лишь своеобразным "театром военных действий" подсознания. Это - страхи. Это - сны. Это - страшные сны. Это - правда. Жестокая. Честная. Правда, о которой вы хотели бы знать, но боялись спросить. Не бойтесь. Анализируйте!', 'Зигмунд Фрейд', '63.50', '9785170723935.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=17'),
(18, 'В поисках потока. Психология включенности в повседневность', 'Это отчасти психологическое исследование, отчасти руководство, помогающее нам стать хозяевами собственной жизни. Опрос тысяч людей показал, насколько часто мы не отдаем себе отчета в том, что происходит в нашей эмоциональной жизни. В результате мы впадаем в одну из двух крайностей: занимаясь делами, мы чаще всего пребываем в стрессе и тревоге из-за работы и множества обязательств, а в моменты досуга - начинаем испытывать скуку. Ключ к решению этой проблемы Чиксентмихайи видит в том, чтобы ставить перед собой задачи, заставляющие максимально задействовать наши умения и таланты и научиться получать радость от полной включенности в повседневные занятия. Уроки книги кажутся простыми, но при этом они действительно показывают путь к максимальной самореализации и счастью.', 'Михай Чиксентмихайи', '99.99', '9785916711721.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=18'),
(19, 'Курс практической психологии. Как разрешить свои проблемы', 'Эта книга для тех, кто хочет научиться преодолевать стресс, раздражительность и депрессию - спутников современного напряженного ритма жизни, читать окружающих, словно книгу, и понимать их еще не высказанное отношение, добиться профессионального роста и изменить свои дела к лучшему, - одним словом, стать успешным как в жизни, так и в бизнесе.', 'Виктор Шапарь', '36.30', '9789661402965.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=19'),
(20, 'Как читать человека', 'Научиться определять, лжет ли ваш собеседник, читать мысли, находить потерянные вещи, противостоять стрессам, успешно подниматься по карьерной лестнице - мечта любого человека, и мечта достижимая! С помощью упражнений и тестов Марка Салема вы разовьете почти фантастические способности - сверхчувствительность, наблюдательность, интуицию, широту мышления, которые позволят вам полностью раскрыть свой потенциал.', 'Марк Салем', '21.50', '9789663439693.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=20'),
(21, 'Гармоничное богатство. Секрет достижения истинного благополучия', 'У нас впереди большая работа. Джеймс Рэй покажет вам совершенно новый способ создавать богатство во всех без исключения областях вашей жизни. К тому времени как вы закончите читать эту книгу, вы будете точно знать, как обрести удовлетворение и изобилие - жизнь, полную истинного благополучия. Именно это автор и называет Гармоничным Богатством.', 'Рэй Джеймс', '42.10', '9785912507519.jpg', 'psychology', 'http://localhost/bookseller/index.php?view=product&id=21'),
(22, 'Java. Руководство для начинающих', 'Книга "Java. Руководство для начинающих" написана Гербертом Шилдтом, автором популярных во всем мире книг по языкам программирования, таким образом, чтобы читатель смог быстро овладеть основными навыками программирования на Java. Полностью обновленное по версии Java Platform, Standard Edition 7, пятое издание этого учебного пособия начинается с рассмотрения самых основ, включая компилирование и выполнение простых программ на Java. Затем в нем описываются ключевые слова и синтаксические конструкции, составляющие основу Java как языка программирования. Далее следует изложение самых передовых языковых средств Java, включая обобщения и многопоточное программирование. Представленный в книге учебный и справочный материал позволяет легко и быстро научиться программировать на Java.', 'Шилдт Герберт', '99.99', '9785845917706.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=22'),
(23, 'JavaScript. Оптимизация производительности', 'Если вы относитесь к подавляющему большинству веб-разработчиков, то наверняка широко применяете JavaScript для создания интерактивных веб-приложений с малым временем отклика. Проблема состоит в том, что строки с программным кодом на языке JavaScript могут замедлять работу приложений. Эта книга откроет вам приемы и стратегии, которые помогут в ходе разработки устранить узкие места, влекущие за собой снижение производительности. Вы узнаете, как ускорить выполнение, загрузку, операции с деревом DOM, работу страницы в целом и многое другое. Николас Закас, программист из компании Yahoo!, специализирующийся на разработке пользовательских интерфейсов веб-приложений, и еще пять экспертов в области использования JavaScript представят оптимальные способы загрузки сценариев и другие приемы программирования, которые помогут вам обеспечить наиболее эффективное и быстрое выполнение программного кода на JavaScript. Вы познакомитесь с наиболее передовыми приемами сборки и развертывания файлов в действующем окружении и с инструментами, которые помогут в поиске проблем.', 'Закас Николас', '99.70', '9785932862131.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=23'),
(24, 'Алгоритмы интеллектуального Интернета. Передовые методики сбора, анализа и обработки данных', 'Важный аспект современных коммерчески успешных приложений - применение методик, позволяющих осуществлять обработку информации и добавлять средства интеллектуальной поддержки. Многочисленные примеры успешных проектов, основанных на применении таких методик, включают такие широко известные бренды, как Google, Netflix и Amazon. Эта книга о том, как построить алгоритмы, формирующие интеллектуальное ядро таких веб-приложений. В книге рассматриваются пять важных категорий алгоритмов: поиск, выработка рекомендаций, создание групп, классификация и ансамбли классификаторов. Исходный код написан на языке Java, тем не менее, программистам, знающим другой объектно-ориентированный язык, вполне по силам разобраться в этом коде и использовать общие принципы с учетом своей специфики. Материал в равной степени применим к различным приложениям - от утилит мобильной связи до традиционных настольных приложений. Издание в первую очередь адресовано программистам и веб-разработчикам, однако множество примеров и новых идей будут полезны и руководителям разного уровня, желающим лучше разобраться в соответствующих технологиях и предлагаемых возможностях с точки зрения бизнеса.', 'Х.Мариманис, Д.Бабенко', '104.60', '9785932861868.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=24'),
(25, 'Работаем на компьютере без ошибок и проблем', 'Самоучители работы на компьютере часто ругают за непонятность. Начинающему пользователю требуются немалые усилия, чтобы соотнести то, о чем написано в книге, с тем, что он видит на экране. А уж когда дело доходит до описания интерфейсов программ, тут и опытному нелегко запомнить все эти сотни меню и кнопок. Здесь же предлагается совершенно иной подход. Текст и иллюстрации здесь связаны в одну структуру, так что, читая про какое-то действие или элемент интерфейса, вы сразу же увидите, как это выглядит на экране. Даже запускать компьютер совсем не обязательно! Вся графика в книге выделена цветом, что предельно упрощает изучение материала (вы сразу видите, что к чему).', 'Шакагов Кирилл', '110.20', '9785699602322.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=25'),
(26, 'Microsoft Windows 8. Руководство пользователя', 'В этой книге описывается последняя версия операционной системы от Microsoft — Windows 8. Рассказывается, как ее установить и настроить, какие возможности эта система предоставляет пользователю, в чем ее отличие от предыдущих версий каковы особенности ее нового графического интерфейса Metro. Даются рекомендации по использованию стандартных программ и мультимедиа-возможностей Windows 8, подключению и работе в Интернете, организации домашней сети и настройке встроенного брандмауэра Windows. Книга рассчитана на пользователей любой квалификации и будет полезна как начинающим, так и достаточно опытным пользователям ПК, ноутбуков и планшетов.', 'Дэн Томашевский', '72.20', '9785845918277.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=26'),
(27, 'Photoshop CS6 для чайников', 'Изучите Photoshop, чтобы делать изображения неповторимыми! Эта книга посвящена версии Photoshop CS6 самого популярного редактора цифровых изображений и методам их правки. В ней рассматриваются основные возможности Photoshop и сопутствующих приложений Camera Raw и Bridge в отношении обработки цифровых изображений, включая выбор формата и разрешения, устранение типичных недостатков фотографий, выполнение различных видов коррекции, обрезку, управление цветом при печати, применение фильтров, слоев коррекции и стилей, ввод, правку и стилевое оформление текста, выделение, раскраску, заливку, ретуширование, работу с векторной графикой, автоматизацию повторяющихся операций и формирование изображений с расширенным динамическим диапазоном. Излагаемый материал дополняется многочисленными иллюстрациями, в том числе и на цветной вклейке, полезными советами и практическими приемами работы с цифровыми изображениями в стандартной и расширенной версиях Photoshop CS6. Сделать идеальный снимок практически невозможно, но его совсем не трудно поправить впоследствии, если знать, как это делается. Из этой книги вы узнаете, как творить с изображениями буквально чудеса средствами Photoshop CS6: от сглаживания морщин и удаления родимых пятен до исключения одних объектов и добавления других в изображение. Прочитав эту книгу, вы быстро научитесь редактировать цифровые изображения и даже создавать потрясающие воображение художественные иллюстрации!', 'Питер Бойер', '99.40', '9785845918123.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=27'),
(28, 'Проектирование процесса проектирования: записки компьютерного эксперта', 'От автора "Мифического человеко-месяца"! Эффективное проектирование лежит в основе любой разработки, начиная от программного обеспечения и заканчивая техническими устройствами и строительными объектами. Но что мы действительно знаем о процессе проектирования? Что приводит к созданию эффективного, изящного проекта? Эти вопросы рассматриваются в книге "Проектирование процесса проектирования: заметки специалиста". В своих новых эссе Фред Брукс изложил исключительно важные идеи, которые позволят сделать для себя интересные открытия проектировщикам в любой области деятельности. Брукс точно определяет особенности, присущие всем проектам разработки, а также показывает, какие процессы и подходы с наибольшей вероятностью позволяют добиться превосходства. На основе общения со многими выдающимися проектировщиками и собственного опыта в нескольких областях проектирования Брукс пришел к заключению, что наилучших результатов позволяют достичь наиболее смелые проектные решения. Автор прослеживает развитие понятия процесса проектирования, рассматривает особенности проектирования при непосредственном общении и дистанционном взаимодействии сотрудников, а также показывает, каковыми на самом являются великие проектировщики. Он исследует тончайшие нюансы процессов проектирования, в том числе бюджетные ограничения многих типов, эстетические требования, условия проведения проектирования и инструментальные средства, подтверждая справедливость своих утверждений на примерах собственных реальных достижений или неудач, от строительства дома до создания операционной системы для IBM/360. На протяжении всей книги Брукс постепенно раскрывает секреты успеха, о которых должен знать каждый проектировщик, руководитель дизайн-проекта и исследователь в области проектирования. Об авторе Фредерик П. Брукс мл. — заслуженный профессор информатики Северокаролинского университета в Чапел-Хилле. Он награжден Национальной медалью США в области технологий за руководство разработкой семейства компьютеров System/360 компании IBM и премией А. М. Тьюринга за "значительный вклад в создание компьютерной архитектуры, операционных систем и разработку программного обеспечения". Он — автор бестселлера "Мифический человеко-месяц": The Mythical Man-Month, Anniversary Edition (Addison-Wesley, 1995).', 'Фредерик Брукс', '99.90', '9785845917928.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=28'),
(29, 'Autodesk 3ds Max 2013. Библия пользователя', 'Как начинающие пользователи, изучающие основы трехмерного моделирования, так и искушенные эксперты, интересующиеся технологическими новинками в данной области, найдут богатый справочный материал в новом издании всемирного бестселлера о программе 3ds Max 2013. В этом прекрасном учебном пособии содержатся полезные практические примеры, даются профессиональные советы и приводятся свыше 150 пошаговых упражнений, которые позволят читателям приобрести необходимые навыки и послужат источником вдохновения. • Приступайте к созданию и анимации учебной модели, воспользовавшись примером из вступительной главы. • Создавайте настраиваемые пользовательские рабочие пространства для решения различных задач и переключайтесь между ними. • Изучите новые инструменты средства Track View. • Преобразуйте каркасные объекты в оптимизированные объекты с помощью инструмента форматирования gPoly. • Освойте новые методы работы с оснасткой, костями и оболочками персонажей. • Создавайте физически реалистичные модели с помощью системы MassFX.', 'Кэлли Мердок', '150.00', '9785845918178.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=29'),
(30, 'NoSQL: новая методология разработки нереляционных баз данных', 'Необходимость обрабатывать все более крупные объемы данных является одним из факторов, влияющих на внедрение нового класса нереляционных баз данных NoSQL. Сторонники баз NoSQL утверждают, что их можно использовать для создания более производительных, легче масштабируемых и проще программируемых систем. Эта книга - краткое, но полное введение в быстро развивающуюся технологию NoSQL. Прамодкумар Дж. Садаладж и Мартин Фаулер объясняют, как работают базы данных NoSQL, и демонстрируют, в каких ситуациях они могут стать более успешной альтернативой традиционным системам RDMBS. Авторы излагают материал в быстром темпе, знакомя читателей с критериями, которые необходимо применять, чтобы принять правильное решение, стоит ли использовать базы NoSQL и какие технологии следует при этом выбирать. Первая часть книги посвящена основным концепциям, включая неструктурированные модели данных, агрегаты, новые модели распределения, теорему САР и отображение-свертку. Во второй части авторы исследуют архитектурные и проектные вопросы, связанные с реализацией баз данных NoSQL. Они также описывают реалистичные сценарии использования, демонстрирующие работу баз данных NoSQL и возможности баз Riak, MongoDB, Cassandra и Neo4j. Кроме того, основываясь на новаторской работе Прамодкумара Садаладжа, авторы книги показывают, как реализовать эволюционное проектирование на основе миграции схем - важный метод, необходимый для применения баз данных NoSQL. Книга завершается описанием новой эры многовариантной персистентности, открытой благодаря технологии NoSQL. В этом мире сосуществуют разнообразные базы данных, и архитектор может выбирать технологию, наилучшим образом подходящую для обеспечения любого вида доступа к данным.', 'Джейсон Лэнгоун, Андрэ Лейбови', '130.25', '9785845918291.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=30'),
(31, 'Виртуализация настольных компьютеров с помощью VMware View 5', 'VMware View - решение для виртуализации настольных компьютеров, которое помогает автоматизировать управление настольными компьютерами и приложениями, сократить затраты и повысить уровень безопасности данных за счет централизации среды настольных компьютеров. Эта книга, написанная известными специалистами Джейсоном Лэнгоуном и Андрэ Лейбовичи, задумана как руководство для архитекторов, поставщиков решений, консультантов, инженеров и вообще всех, кто занимается проектированием и внедрением решений на базе VMware View 5. В ней рассматриваются реальные сценарии, поскольку лучших примеров для обучения, пожалуй, не сыскать. Объясняется, как сконфигурировать систему и задать параметры, чтобы добиться успеха, а также почему следует поступать именно так, а не иначе.', 'Джейсон Лэнгоун, Андрэ Лейбови', '102.55', '9785940749042.jpg', 'computer_books', 'http://localhost/bookseller/index.php?view=product&id=31'),
(32, 'Франкенштейн, или современный Прометей', '"Франкенштейн, или Современный Прометей" появился после того, как в 1816 году лорд Байрон предложил своим гостям, девятнадцатилетней Мэри и ее будущему мужу Перси Шелли, написать историю о привидениях. Роман, сюжет которого явился Мэри Шелли в ночном кошмаре, был впервые анонимно опубликован в 1818 году, а в 1631-м переиздан под настоящим именем автора. Эта книга - первое фантастическое произведение в мировой литературе и несравненная классика готического жанра.', 'Мэри Шелли', '55.30', '9785389028654.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=32');
INSERT INTO `products` (`id`, `title`, `description`, `author`, `price`, `image`, `cat`, `link`) VALUES
(33, 'Милый друг', 'Литературная слава автора "Милого друга" и "Жизни" феноменальна: она оказалась заслуженной и непреходящей, выдержала нападки недальновидных современников и испытание временем. Незадолго до той поры, когда Мопассан начинал свой писательский путь, сама природа Франции, казалось, грезила свежими изобразительными средствами, новыми темами. Искусство не заставило себя долго ждать, обратив пристальное внимание на текучесть и полутона, на оттенки чувств и переливы настроений, на размытость и мимолетность любовных отношений. Все эти участники и участницы завтраков на траве, все эти соискатели места под солнцем и в тени у реки в Аржантейе - должны были дождаться и дождались своего художника в прозе, который уловил и воссоздал неповторимую атмосферу Франции второй половины XIX столетия. Мопассан тонко и виртуозно фиксировал душевные, общественные и природные вибрации, исследовал свойства страсти. Его герои обретались в особой системе координат - между внешним светским лоском и сложными внутренними мотивами, между открытой чувственностью и скрытыми побуждениями, между искренностью и лицемерием, между любовью и болью. Жизнь героев Мопассана становится расплатой за призрачный миг счастья или за мечту о нем. Романы и новеллы Мопассана отличаются удивительной психологической глубиной, эстетическим совершенством, за которыми стоят великий талант и исключительное изобразительное мастерство художника.', 'Ги Де Мопассан', '43.10', '9785389032972.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=33'),
(34, 'Декамерон', '"Декамерон" Дж.Боккаччо - одна из жемчужин литературы эпохи Возрождения, собрание удивительно ярких, сочных новелл, воспевающих жизнь во всех ее чувственных проявлениях. Во время чумы 1348 года десять молодых людей затворяются на загородной вилле, рассказывая друг другу различные истории.', 'Джованни Бокаччо', '37.00', '9785389012103.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=34'),
(35, 'Кобзар', 'Кобзар — так назвав український народ Тараса Григоровича Шевченка. Ім’я цього всесвітньо відомого поета й митця є уособленням незламної сили духу та пророчого дару. Геній Великого Кобзаря світить нам крізь століття. Цілі покоління українців виховувалися на його творах. І сьогодні Шевченкова поезія продовжує надихати нас на нові звершення й перемоги. Це видання включає поетичні твори, написані Кобзарем з 1837 по 1861 рік. Видання адресоване всім шанувальникам українського поетичного слова.', 'Тарас Шевченко', '94.90', '9789664818756.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=35'),
(36, 'Василий Теркин: книга про бойца', 'Александр Трифонович Твардовский в годы Великой Отечественной войны был корреспондентом фронтовой газеты. Его поэму "Василий Теркин", созданную на полях сражений, в самой гуще военных событий, ждал поистине всенародный успех. "Теркина" заучивали наизусть, ему подражали. Главный ее персонаж - именно народный герой, балагур и весельчак, образец удали и отваги. Поэма Твардовского, пронизанная юмором, - честная и правдивая книга о войне. В письмах с фронта бойцы благодарили автора и признавались, что хотели бы "воевать и закончить войну вместе с Теркиным".', 'Александр Твардовский', '31.16', '9785389043459.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=36'),
(37, 'Собрание сочинений. Джек Лондон', '', 'Джек Лондон', '93.50', '9789661442640.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=37'),
(38, 'Записки Патрика', 'Основные собранные в настоящем сборнике тексты Б. Пастернака можно назвать "попыткой Романа". На многих страницах этой прозы мерцают образы и сюжетные линии будущей Главной прозаической книги поэта. Самые серьезные подступы к Роману предстают перед нами в "Записках Патрика" (1937). "Я давно, все последние годы, мечтал о такой прозе, которая, как крышка бы на ящик, легла на все неоконченное и досказала бы все фабулы мои и судьбы…" "Роман Пастернака, судя по отрывкам, превосходен. Сжатые фразы, необычайная образность, простота, размах событий и охват…" - так писал А. Афиногенов в своем дневнике о прозе Пастернака. Кроме "Записок Патрика" в сборник входят повесть "Письма из Тулы", "Повесть", "История одной контроктавы" и др.', 'Борис Пастернак', '22.20', '9785998506802.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=38'),
(39, 'Люблю глаза твои, мой друг', 'В 1850-м в журнале "Современник" была воспроизведена подборка стихов Тютчева, некогда опубликованных Пушкиным, и напечатана статья Н. Некрасова, в которой он причислил эти стихи к блестящим явлениям русской поэзии и поставил Тютчева в один ряд с Пушкиным и Лермонтовым. Спустя четыре года в приложении к "Современнику" было опубликовано 92 стихотворения Тютчева, а затем по инициативе И.Тургенева был издан его первый поэтический сборник. Славу Тютчева подтвердили многие его современники - Тургенев, А. Фет, А. Дружинин, С. Аксаков, А. Григорьев… Л. Толстой называл Тютчева "одним из тех несчастных людей, которые неизмеримо выше толпы, среди которой живут, и потому всегда одиноки"... Значительную часть жизни (двадцать два года) Тютчев провел за границей (из них двадцать - в Мюнхене). Там были созданы шедевры его философской и политической лирики, лучшие стихотворения о любви и природе. В Россию Тютчев вернулся сложившимся поэтом. В настоящее издание включены все самые известные стихотворения Ф. И. Тютчева. В качестве послесловия дана статья Ю. Тынянова "Вопрос о Тютчеве".', 'Федор Тютчев', '22.20', '9785389027282.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=39'),
(40, 'Двенадцать стульев', 'У "Двенадцати стульев" И.Ильфа и Е.Петрова завидная судьба. Этот роман был создан талантливым писательским дуэтом почти 90 лет тому назад, но до сих пор остается бестселлером. Многие фразы, звучащие из уст его героев, стали афоризмами, роль обаятельного мошенника Остапа Бендера в разнообразных экранных версиях сыграли лучшие актеры, но всякий раз "Двенадцать стульев" - открытие. Этот роман по-прежнему очаровывает своим юмором, он свеж, оригинален и непредсказуем.', 'Илья Ильф, Евгений Петров', '24.70', '9785389021020.jpg', 'fiction', 'http://localhost/bookseller/index.php?view=product&id=34'),
(41, 'Кабала святош', 'М.Булгаков - ключевая фигура в русской литературе XX века, автор знаменитых во всем мире произведений "Мастер и Маргарита", "Дни Турбиных", "Дьяволиада", "Собачье сердце", "Роковые яйца", "Бег", "Иван Васильевич", "Записки на манжетах", "Записки покойника". В конце 1920 - начале 1930-х годов важное место в творчестве Булгакова занимает мольеровская тема. Роман-биография "Жизнь господина де Мольера", перевод комедии великого французского комедиографа Жана Батиста Мольера "Скряга", а также пьесы "Кабала святош" и "Полоумный Жерден", созданные в этот период, составляют так называемую булгаковскую "мольериану", одной из главных проблем которой являются взаимоотношения художника с властью, точнее, с господствующей идеологией во всех ее проявлениях, - проблема, знакомая Булгакову не понаслышке. В настоящем издании представлена драматическая часть "мольерианы". Издание снабжено обширной статьей и комментариями В.И.Лосева.', 'Михаил Булгаков', '22.20', '9785389031982.jpg', 'fiction', ''),
(42, 'Афоризмы', 'Фридрих Ницше — немецкий философ, филолог-классик, поэт, автор знаменитых трудов: "Так говорил Заратустра", "Генеалогия морали", "Падение кумиров", "Ессе Ното", "Воля к власти" и многих других, одной из ярких черт которых является афористичность. В настоящем издании составителем И. Кивель предпринята попытка представить наиболее яркие и актуальные для современности высказывания замечательного мыслителя и прозорливца Фридриха Ницше. Для настоящего издания поиск велся по всем работам философа, в основном в сборник вошли афоризмы из его чисто философских работ, таких как "По ту сторону добра и зла", "Падение кумиров", "Человеческое, слишком человеческое", "Странник и его тень".', 'Фридрих Ницше', '21.00', '9785389010703.jpg', 'fiction', ''),
(43, 'Любовный напиток', 'В поэтический сборник "Любовный напиток" включены бессмертные рубаи Омара Хайяма, классика персидско-таджикской поэзии, великого ученого Средневековья - философа, математика, астронома. Его рубай стали всемирно известны. Омара Хайяма читают и перечитывают, его переводят и ему подражают. В России интерес к Хайяму не угасает с конца XIX века. В настоящем издании представлены переводы, выполненные замечательным ленинградским поэтом и переводчиком Глебом Семеновым.', 'Омар Хайям', '21.00', '9785389049857.jpg', 'fiction', ''),
(44, 'Две поэмы', 'В конце тридцатых годов, после "страха, скуки, пустоты, смертного одиночества", Ахматова вновь начала писать. Создание поэмы "Путем всея земли" ("Китежанка") и начало работы над "Поэмой без Героя" относятся к 1940 году. "Путем всея земли" - автобиографическая поэма, в ней автором спустя годы отражены и осмыслены события детства и юности. "Поэма без Героя", как отмечают исследователи, является "творческим синтезом поэтического развития Ахматовой", "монологом автора, свидетеля и участника изображаемых событий" (В. Жирмунский), "летописью событий XX века" (А. Найман), и при этом поэма обладает "всеми чертами лирико-драматического произведения" (В. Мусатов). Но главным остается то, что "Поэма без Героя" - один из шедевров русской литературы XX столетия. Серия "Настоящая Ахматова" отличается от всех предшествующих изданий стихотворений и поэм А. Ахматовой прежде всего качеством.', 'Анна Ахматова', '55.60', '9785389015227.jpg', 'fiction', ''),
(45, 'Муму', 'Классик мирового уровня, писатель, поэт и переводчик, Иван Сергеевич Тургенев по праву занимает почётное место в плеяде лучших писателей Золотого века русской словесности. Его знаменитые произведения, уже давно ставшие достоянием отечественной литературы, воспитывают в поколениях любовь и уважение к великому русскому языку и русской культуре.', 'Иван Тургенев', '67.80', '9785389019850.jpg', 'fiction', ''),
(46, 'Мертвые души', '"...Говоря о "Мертвых душах", можно вдоволь наговориться о России", - это суждение поэта и критика П.А.Вяземского объясняет особое место поэмы Гоголя в истории русской литературы: и огромный успех у читателей, и необычайную остроту полемики вокруг главной гоголевской книги, и многообразие высказанных мнений, каждое из которых так или иначе вовлекает в размышления о природе национального мышления и культурного сознания, о настоящем и будущем России.', 'Николай Гоголь', '24.70', '9785389013223.jpg', 'fiction', ''),
(47, 'Отверженные', 'Один из лучших романов классика мировой литературы. Жан Вальжан, Козетта, Гаврош - имена героев романа давно сделались нарицательными, число его читателей за полтора столетия с момента выхода книги в свет не становится меньше, роман не теряет популярности. Калейдоскоп лиц из всех слоев французского общества первой половины XIX столетия, яркие, запоминающиеся характеры, гротеск и романтика, сентиментальность и реализм, напряженный, захватывающий сюжет - главные причины успеха книги Гюго и они же главные причины частого обращения к ней мирового кинематографа.', 'Виктор Гюго', '81.80', '9789661448369.jpg', 'fiction', ''),
(48, 'Афоризмы житейской мудрости', '"Афоризмы житейской мудрости" А.Шопенгауэра - одно из наиболее значительных и популярных этических произведений в новоевропейской философии. Опирающийся на основное сочинение Шопенгауэра "Мир как воля и представление", этот трактат дает яркую и целостную картину философского восприятия жизни, с его отношением к воспитанию, долгу, судьбе, одиночеству.\r\n', 'Артур Шопенгауэр', '22.20', '9785389019461.jpg', 'fiction', ''),
(49, 'Одноэтажная Америка', 'Илья Ильф и Евгений Петров - знаменитые советские фельетонисты и писатели-сатирики первой половины XX века. В соавторстве они создали известнейшие романы "Двенадцать стульев" и "Золотой теленок", повесть "Одноэтажная Америка", а также несколько циклов новелл. Повесть "Одноэтажная Америка" - результат поездки авторов в Соединенные Штаты в середине 1930-х годов, воплощение их поисков "настоящей Америки", их личное открытие Америки. Книгу предваряет вступительная статья Александры Ильиничны Ильф, дочери одного из авторов.', 'Илья Ильф, Евгений Петров', '34.60', '9785386049164.jpg', 'fiction', ''),
(50, 'Каштанка', 'К рассказам Чехова будут обращаться всегда. Читаются они необыкновенно легко, потому что мастерство писателя непревзойдённо. Он пишет кратко, но ёмко. Смеётся весело, но с оттенком грусти. Учит не просто чувствовать, а чувствовать благородно, вызывая то улыбку, то слёзы сострадания. И ничего не придумывает, воспроизводя сюжеты из самой жизни. Его слова звучат тихо, но попадают в самую душу. Он не читает мораль, не бранит, не наказывает за злодеяния. Он понимает, что победить можно только смехом, добрым примером и сочувствием. Без книг Антона Павловича Чехова ваша библиотека будет неполной!', 'Антон Чехов', '67.80', '9785389019812.jpg', 'fiction', ''),
(51, 'Король Генрих IV', 'Историческая хроника-дилогия Уильяма Шекспира "Король Генрих IV" (1597-1598) переносит читателя в полную мятежных страстей и кровавых конфликтов политическую жизнь Англии начала XV века. Приход к власти короля Генриха IV - первого из династии Ланкастеров - отмечен появлением на подмостках Истории целой вереницы ярких, колоритных, запоминающихся героев, среди которых юный принц Гарри, сэр Генри Перси по прозванию Хотспер ("Горячая Шпора") и, конечно, рыцарь-шут сэр Джон Фальстаф - величайший из созданных Шекспиром комических персонажей. Публикация текста хроники сопровождается подробным историко-литературным комментарием, подготовленным специально для настоящего издания. Перевод с английского В. Морица, М. Кузмина.', 'Уильям Шекспир', '24.70', '9785998504129.jpg', 'fiction', ''),
(52, 'Семейная медицинская энциклопедия', 'В энциклопедии в наиболее доступной форме собрана вся необходимая информация, которая может понадобиться любому человеку при осуществлении профилактики и лечении в домашних условиях наиболее распространенных болезней и недомоганий. Широко представлены лекарственные препараты и средства, медикаментозные и народные методы лечения.', 'Сергей Зайцев', '82.50', '9789851702349.jpg', 'medicine', ''),
(53, 'Начало жизни вашего ребенка', 'Доступная и увлекательная книга, написанная практикующим врачом-педиатром и рассказывающая о наиболее сложном и ответственном этапе в жизни ребенка и его родителей. Простые решения, казалось бы, сложных задач. Ответы на вопросы о том, что такое хорошо и что такое плохо, рекомендации, позволяющие принять правильные решения, и советы, способствующие укреплению нервной системы мам, пап, бабушек и дедушек.', 'Комаровский Евгений', '33.50', '9789662065022.jpg', 'medicine', ''),
(54, 'Почему я все время хочу есть?', 'Эта книга является итогом многолетних научных, психологических и метафизических исследований о переедании и тяге к еде. Благодаря ей вы сможете интерпретировать и контролировать свою эмоционально обусловленную тягу к пище. Это книга для всех, кто хочет похудеть и поддерживать нормальный вес путем естественного процесса уменьшения аппетита.', 'Дорин Вирче', '86.70', '9789851516946.jpg', 'medicine', ''),
(55, 'Первый год жизни ребенка', 'В книге, рассчитанной на молодых мам и пап, в популярной форме изложены практические советы по уходу, лечению и воспитанию ребенка первого года жизни. Автор - опытный педиатр - при написании этой книги использовал не только знание работ лучших отечественных и зарубежных врачей, но и собственный опыт отца двух сыновей.', 'Сергей Зайцев', '85.40', '9789851706538.jpg', 'medicine', ''),
(56, 'Исцеление сексом: активизация даосских точек любви', 'Эта книга объясняет, как, используя сексуальные рефлекторные точки, превратить любовную близость в сеанс исцеления. Здесь описаны техники, позволяющие определить совместимость партнеров; упражнения и массажи для устранения сексуальной слабости и дисбаланса; целительные позы, способствующие лечению многих заболеваний; а также множество другой ценной информации из древних китайских трактатов. Мы надеемся, что эта книга поможет вам увидеть сексуальную энергию - главную движущую силу Вселенной - с новой точки зрения и использовать ее законы для создания гармоничной жизни, исполненной радости и любви.', 'Мантэк Чиа', '43.10', '9785399003399.jpg', 'medicine', ''),
(57, 'Библия секса', 'В этой книге откровенно рассказывается о том, чем является секс для современного человека.Важно, что она написана не просто журналистом и психотерапевтом, а прежде всего женщиной и мужчиной, что позволяет им говорить о взаимоотношении полов с двух, дополняющих друг друга точек зрения.', 'Збигнев Кветковский', '41.00', '9789660350694.jpg', 'medicine', ''),
(58, 'Уникальный метод восстановления зрения', 'Если хотите вернуть и сохранить здоровье, немедленно снимайте очки и начинайте восстанавливать зрение по методу вибрационного исцеления профессора Панкова, основы которого автор разрабатывал более 30 лет. Тысячи его пациентов всех возрастов без лекарств и операций избавились от близорукости, дальнозоркости, астигматизма, косоглазия, катаракты и глаукомы, проблем с сетчаткой с помощью оригинальных упражнений с настольной лампой, фонариком и свечой, являющихся краеугольным камнем его системы. Читатель легко освоит простые и доступные рекомендации школы Панкова, основанной на использовании света и цвета, которые всего за 30 дней заново научат больные глаза видеть.', 'Олег Панков', '99.99', '9785271326844.jpg', 'medicine', ''),
(59, 'Гомеопатия доктора Синельникова', 'В этом издании гомеопатических средств есть все необходимое для оптимального подбора лекарств, которые окажут правильное и щадящее действие на организм. Автор книги предлагает по симптомам, которые приводятся в специальном разделе, определить свое заболевание и подобрать лечебные средства. Полезные рекомендации и ценные советы опытного врача помогут одолеть любой недуг.', 'Валерий Синельников', '99.99', '9785227026958.jpg', 'medicine', ''),
(60, 'Мой Мистер Судьба, или 9 месяцев', 'Сама автор произведения называет его психологическим романом о жизни молодой девушки, которая мечтает встретить свою Любовь. Визуализируя образ будущего любимого мужчины, она вскоре встречает его в толпе прохожих, бросает прошлую жизнь и едет с ним в Москву. А стоит ли игра свеч? Достоин ли мужчина таких жертв? Может ли нарисованный в голове образ быть идеальным в жизни? На все эти вопросы можно будет найти ответ в романе «Мой Мистер судьба или 9 месяцев». «Мы постоянно ищем что-то или кого-то, способного нас осчастливить, заставить почувствовать гармонию, вкус к жизни, а ведь на самом деле сделать себя счастливыми можем только мы сами. Эта книга-откровение о поиске себя в жизни и жизни в себе. Я искренне надеюсь, что читатель найдет себя в этой книге, а возможно, и выход из некоторых жизненных ситуаций», - отмечает сама Лэнтиджини.', 'Лэнтиджини Ирэн', '99.99', '9789667889913.jpg', 'audio_books', ''),
(61, 'Захист прав споживачів. Нормативно-правове регулювання', 'Захист прав споживачів. Нормативно-правове регулювання', 'Роїна Олена', '27.00', '9789661638661.jpg', 'legal_literature', ''),
(62, 'Кодекс господарський 2012', 'Кодекс господарський 2012', 'Людмила Потемкина', '16.65', '9786175922590.jpg', 'legal_literature', ''),
(63, 'Кримінально-процесуальний Кодекс', 'Кримінально-процесуальний кодекс України визначає порядок провадження у кримінальних справах, він забезпечує охорону прав та законних інтересів фізичних і юридичних осіб, а також швидке і повне розкриття злочинів, викриття винних та забезпечення правильного застосування покарання з тим, щоб злочинці були притягнуті до відповідальності і жоден невинний не був покараний. Розрахований на слідчих, суддів, адвокатів, прокурорів, дізнавачів, інших практичних працівників органів внутрішніх справ, прокуратури, суду та адвокатури, співробітників СБУ та податкової міліції, студентів, слухачів і курсантів вищих юридичних навчальних закладів, що навчаються за спеціальністю "Правознавство" та "Правоохоронна діяльність".', '', '21.80', '9786175920800.jpg', 'legal_literature', ''),
(64, 'Кодекс України про адміністративні правопорушення', 'Кодекс України про адміністративні правопорушення регулює охорону прав і свобод громадян, власності, конституційного ладу України, прав і законних інтересів підприємств, установ і організацій, встановлений правопорядок, забезпечує зміцнення законності, запобігання правопорушень, виховує громадян у дусі точного і неухильного додержання Конституції і законів України. Видання буде корисним для суддів, адвокатів, працівників правоохоронних та інших державних органів, юристів, які працюють на підприємствах, в організаціях, установах, студентів, аспірантів і викладачів вищих навчальних закладів юридичного профілю, а також для всіх, хто цікавиться проблемами застосування законодавства України про адміністративні правопорушення.', '', '21.80', '9789663399744.jpg', 'legal_literature', ''),
(65, 'Все о бухучете для начинающих. Теория и практика', 'Вопросы ведения бухгалтерского учета раскрываются от простого к сложному, начиная с азов - теории бухучета - и завершая рассмотрением всех практических вопросов бухгалтерского учета с элементами налогового учета. Ценным является и то, что в издании учтены все изменения и дополнения, произошедшие в 2008 г. в законодательстве, регулирующем бухгалтерский учет. Материал изложен в доступной форме, многие положения не только теоретически обоснованы, но и подкреплены примерами, поэтому начинающие самостоятельно овладевать знаниями в области бухгалтерского учета специалисты могут использовать данное издание в качестве самоучителя. Для бухгалтеров-практиков книга послужит инструментом, позволяющим быстро найти необходимую информацию, в частности перечень основных нормативно-правовых актов по каждой из рассмотренных тем. А руководителям приведенные в книге материалы помогут в принятии решений управленческого характера. Издание адресовано бухгалтерам-практикам, всем, кто изучает бухгалтерский учет на различных курсах и самостоятельно, студентам экономических и неэкономических ВУЗов, а также руководителям предприятий и организаций.', 'Анна Швец', '99.99', '9789856826149.jpg', 'legal_literature', ''),
(66, 'Ипотека. Кредит. Комментарий жилищного законодательства', 'Книга написана в соответствии с современным жилищным законодательством и законодательством, регулирующим жилищную ипотеку, которая в самые ближайшие годы должна стать одним из приоритетных и перспективных направлений развития рынка доступного жилья в России. Что такое ипотечное жилищное кредитование, каковы его плюсы и минусы? Какие бывают ипотечные кредиты и какой кредит с учетом конкретных жизненных обстоятельств и состава семьи выгоднее брать? Какова структура рынка ипотечного жилищного капитала? Что представляют собой этапы ипотечного жилищного кредитования? На все эти и многие другие вопросы можно найти квалифицированные и лаконичные ответы в данной книге, авторы которой - практикующие юристы.', 'Л. Грудцына, М. Козлова', '53.10', '9785699138357.jpg', 'legal_literature', ''),
(67, 'Сказки', 'Сказки А.С.Пушкина - это те произведения для детей, с которых начинается большая любовь к классической русской поэзии. Написанные в народном духе и стихотворной форме, они всегда вызывают восхищение. "Что за прелесть эти сказки! Каждая есть поэма..." Обо всём этом и не только в книге Сказки. Пушкин (нов.обл.*) Золотая коллекция детства (А. С. Пушкин)', 'Александр Пушкин', '64.10', '9785389035423.jpg', 'childrens_books', ''),
(68, 'Королевство кривых зеркал', 'Сказка о приключениях маленькой школьницы Оли и девочки Яло из Зазеркалья в волшебном королевстве, где властвует жестокость, столкнувшись с которой девочки учатся по-новому ценить доброту, верность, дружбу. Вам предстоит вступить в схватку с ужасными злодеями, выручить из беды хороших людей и даже победить всесильных правителей. Не теряйте времени, вперед к новым приключениям и настоящим победам! Повесть-сказка Виталия Георгиевича Губарева переносит читателей в невероятный мир фантастики и приключений.', 'Губарев Виталий', '70.30', '9785180011459.jpg', 'childrens_books', ''),
(69, 'Маленький Мук и другие сказки', 'Сказки немецкого писателя Вильгельма Гауфа знакомы нам с самого детства. Маленький Мук, Калиф-Аист, Карлик Нос - это герои его удивительных сказок, полных невероятных и опасных приключений, колдовства и чудес. Герои если и попадают в затруднительные ситуации, то ненадолго, они чудесным образом освобождаются от злых чар, добро и справедливость торжествуют.', 'Вильгельм Гауф', '49.30', '9785389007833.jpg', 'childrens_books', ''),
(70, 'Русские сказки о животных', 'В книге представлены русские народные сказки. Для чтения взрослыми детям.', 'Елена Рерих', '46.00', '9785389011472.jpg', 'childrens_books', ''),
(71, '100 знаменитых сказок мира', 'Это солидное издание с красочными цветными иллюстрациями станет вашей любимой книгой для семейного чтения на много-много лет. Ведь здесь собраны народные сказки разных стран и литературные шедевры мастеров-сказочников: Шарля Перро, Ханса Кристиана Андерсена, братьев Гримм, Сельмы Лагерлеф, Редьярда Киплинга и др. Вместе с героями сказок юный читатель совершит 100 захватывающих путешествий по самым волшебным уголкам мира. Он побывает в славном городе Багдаде, преодолеет сотни миль вместе с Нильсом и дикими гусями, спасет замерзшую ласточку с крошкой Дюймовочкой, покорит чертог Снежной королевы и подружится с совсем нестрашным Кентервильским привидением. Приключения начинаются!', '', '70.10', '9789661439381.jpg', 'childrens_books', ''),
(72, 'Тысяча и одна ночь. Арабские сказки', 'Сказки Востока всегда манили к себе своей загадочностью и мудростью, увлекательными описаниями удивительных стран, живостью и глубиною переживаний героев, ясной, определенной моралью. Из восточных сказок можно почерпнуть много полезного, а самое главное - развлечь и заинтересовать своего ребенка.', '', '55.50', '9785389007819.jpg', 'childrens_books', ''),
(73, 'Рапунцель', 'Одна пара жила по соседству с колдуньей. Однажды беременная жена увидела, что у соседки растёт рапунцель и попросила мужа добыть ей этого лакомства. Муж решил украсть листьев для жены, но колдунья поймала его и разрешила брать у неё рапунцеля сколько угодно в обмен на обещание отдать ей первенца. Когда у жены родилась девочка, колдунья забрала её в падчерицы и назвала Рапунцель. Когда Рапунцель достигла двенадцати лет и оказалась очень красивой девушкой, мачеха заперла её в башне в лесу. В башне не было дверей, только одно окно на вершине и чтобы добираться до падчерицы, колдунья звала ее. Тогда Рапунцель свешивала вниз свои длинные золотистые волосы, и колдунья взбиралась по ним. Однажды некий принц обнаружил башню, забрался к девушке и предложил ей стать его женой. Рапунцель согласилась, но её мачеха, узнав об этом, отстригла ей косы и из башни выгнала в чащу леса, а принца ослепила.', 'Братья Гримм', '80.10', '9785389018495.jpg', 'childrens_books', ''),
(74, 'Принцессы со всего света', 'Принцессы всего мира приглашают вас в сказочное кругосветное путешествие! Вас ждут заморские страны и чудесные приключения, мир, полный волшебства и таинственных грез… Самые прекрасные легенды о принцессах разных стран и народов!', 'Гуайє Кателль', '99.99', '9785389009523.jpg', 'childrens_books', ''),
(75, 'Сказки на ночь малышам', 'Чтение стихов и сказок на ночь - это своего рода, пожелание спокойной ночи ребенку. Тихий, спокойный голос успокаивает и убаюкивает малыша, а короткий, добрый и ненасыщенный действиями и эмоциями сюжет поможет в этом. Сказка на ночь - это маленькие безопасные уроки жизни. В этот момент для маленького слушателя в целом мире существует только ваш голос, и что он увидит во сне всецело зависит только от того, что он услышит и почувствует перед сном. Так пожелайте же вашему малышу спокойной ночи и приятных сновидений не сухими двумя словами, а добрыми стихами и сказками. Художники: Мишин. Ю. Шляхов И. Смирнова Е. Ермакова Е. Поркшеева М. Для дошкольного и младшего школьного возраста', '', '58.00', '9785378006663.jpg', 'childrens_books', ''),
(76, 'Колобок', 'Всем известная и любимая русская народная сказка "Колобок" с изумительными иллюстрациями Анны Вронской.', '', '43.10', '9785389019027.jpg', 'childrens_books', '');
INSERT INTO `products` (`id`, `title`, `description`, `author`, `price`, `image`, `cat`, `link`) VALUES
(77, 'Большая книга Незнайки. Незнайка на Луне', 'В нашей стране нет ни одного человека, кто не знал бы имени Николая Николаевича Носова. На книгах этого замечательного автора выросло уже несколько поколений юных читателей, которые, повзрослев, с удовольствием покупают книги Носова своим детям и внукам. Вместе с ними они с упоением перечитывают смешные и поучительные рассказы про фантазёров и живую шляпу, весёлые истории про Витю Малеева и Колю Синицына. Сказочная трилогия про Незнайку и его друзей - классика детской литературы, она навсегда вошла в её золотой фонд, став национальным достоянием России. В фантастической повести «Незнайка на Луне» вы прочитаете о космических приключениях Незнайки и его друзей, которые решили отправиться в путешествие на Луну.', 'Николай Носов', '99.99', '9785389017801.jpg', 'childrens_books', ''),
(78, 'Лучшие меню', 'В книге "Лучшие меню" знаменитого шеф-повара Гордона Рамзи представлена кухня разных стран и регионов: итальянская, французская, испанская, средиземноморская, британская, китайская, кухня Юго-Восточной Азии, индийская, марокканская, мексиканская, ближневосточная. Гордон предлагает меню, составленные из его любимых национальных блюд, а также сезонные меню, созданные под влиянием разных кулинарных традиций. Всего их пятьдесят два, и в каждом - закуска, основное блюдо и десерт. Рецепты собраны в виде кулинарного "конструктора": страницы разрезаны на три части, так что вы сможете либо воспользоваться готовым меню, либо без особого труда составить свое собственное. Итак, вариантов множество - на любой случай, для любого времени года. Перед вами открываются удивительные возможности, ограниченные только вашей фантазией. Готовьте по рецептам Гордона Рамзи и придумывайте свои меню - столько, сколько захотите. Рецепты, в которых учитывается сезонность продуктов, понятны и просты, по ним легко готовить. Роскошные фотографии блюд всегда у вас перед глазами - вам не придется перелистывать страницы, чтобы взглянуть на них. Книгу дополняют рецепты превосходных гарниров, базовых блюд и заготовок. В общей сложности в книге собрано сто девяносто великолепных рецептов.', 'Гордон Рамзи', '99.99', '9785389047549.jpg', 'cooking', ''),
(79, 'Итальянская кухня. Кухни народов мира №3', 'Если пожить в Италии пару недель и просто иногда включать телевизор, то скоро начнешь готовить. Потому что кулинарные передачи - это основной дневной жанр программ. И они все делают с таким азартом, огоньком, танцами, песнями, так все просто и легко, что хочешь не хочешь заражаешься. Итальянская кухня - это вкусный рай для всех других. У меня от нее даже токсикоз серьезно уменьшался. Вот только самих итальянцев мне немного жаль: у них такие нежные желудочки, что, например, нашу селедку под шубой им можно давать лишь в гомеопатических дозах. А иначе они болеют потом два дня.', 'Мария Распутина', '25.00', '9789662320626.jpg', 'cooking', ''),
(80, 'Я и моя мультиварка', 'Супы и десерты, салаты и пироги, хлеб и йогурт - в мультиварке можно готовить все! Эта небольшая кастрюлька может заменить целую кухню и значительно сэкономить время. Новая книга Анны Китаевой послужит отличным пособием для начинающих знакомство с мультиваркой, а для тек, кто уже оценил по достоинству все возможности "чудо-кастрюльки" в книге найдется интересные рецепты, которые универсальны для любой модели мультиварки.', 'Анна Китаева', '99.99', '9785699606078.jpg', 'cooking', ''),
(81, 'Мангал', 'Победитель парижского конкурса кулинарных книг, обладатель диплома за лучшую в мире книгу о национальной кухне 2012-го года Сталик Ханкишиев представляет свою новую книгу о шашлыке - первую, из новой серии, в которой автор решил более пристально рассмотреть главные инструменты восточной кухни - мангал, казан и тандыр, и рассказать все о таких великих блюдах, как плов, шашлык, шурпа и сотнях других шедевров, украшающих мировую кулинарию. Новую серию отличает еще большая детальность в описаниях блюд, фотографии, роскошнее прежних и системность, ставящая точку в описании кухни Средней Азии и Азербайджана. "Первые три книги были обо всем понемногу, за исключением второй, которая целиком была посвящена баранине. Новая книга рассматривает только одну тему: мангал, шашлык. Поэтому это очень большая книга! Новые тексты, не входившие в предыдущие книги, составляют 80%. Я сделал, по сути, принципиально новую книгу. Старые тексты и фотографии, если они где и встречаются, теперь просто стоят на своих местах и вы сами убедитесь, что без них книга была бы неполной. И смотрятся они теперь настолько же по-другому, насколько материалы из ЖЖ иначе смотрятся в моих первых трех книгах. Новая книга, если честно, намного лучше сформирована, нежели предыдущие. Просто за семь лет я кое-чему научился, тому свидетельство - первое место за лучшую в мире книгу о национальной кухне 2012 года на международном конкурсе в Париже. Книга разбита на три главы: Ученик, Мастер и Профессор. По названию глав, я думаю, все понятно, правда? То есть, постепенно, от простого к сложному, читателю рассказывается об смысле этого блюда и технологиях его приготовления. Но уверяю, что и самые опытные любители шашлыка найдут для себя много интересного и нового даже в первой главе. Не стоит считать, сколько в каждой главе рецептов. Рецепты служат лишь иллюстрациями к рассказу о главном - о том, как самому можно творчески подходить к приготовлению шашлыка и еды вообще. Наконец-то моя книга полностью избавлена от граммов и минут. Цифры и параметры в книге возникают только там, где без них совсем уж никак не обойтись, но тут же рассказывается о том, как их забыть и жить счастливо без таблиц и длинных списков, убивающих всякое творчество. К моменту издания своей первой книги, я кое-что знал о шашлыке. Но за прошедшие семь лет я узнал о шашлыке столько, сколько не узнал за всю свою предыдущую жизнь. Поэтому эта книга... написана не до конца. Она обрывается на самом интересном месте. Я надеюсь, что вариантов ее окончания будет ровно столько, сколько экземпляров будет отпечатано и продано. Мне хочется верить, что после прочтения этой книги вы полюбите шашлык не меньше меня и будете знать об этом блюде не меньше меня. При этом, даже тот, кто никогда не готовил шашлык прежде, будет готов к самостоятельному полету мысли - самому интересному, что только может быть в этой жизни."', 'Сталик Ханкишиев', '123.60', '9785170776870.jpg', 'cooking', ''),
(82, 'Мясо. Лучшие блюда. Готовьте, как профессионалы!', 'Последние тенденции в отечественной кулинарии заставляют по-новому взглянуть на такой популярный в нашей стране продукт, как мясо. Котлеты и биточки отходят на задний план, уступая место национальным блюдам разных стран и народов, оригинальным находкам кулинаров-экспериментаторов, неожиданным открытиям профессиональных шеф-поваров. Стейки и блюда на гриле вызвали сегодня настоящий бум, и армия их поклонников, почитателей и даже фанатов растет буквально с каждым днем. Эта книга, в которой собраны рецепты лучших мясных блюд, разработанные шеф-поварами знаменитых ресторанов, станет для них настоящим подарком.', 'Оринцов', '99.99', '9785271308154.jpg', 'cooking', ''),
(83, 'Украинская кухня. Лучшие рецепты', 'Семь важных причин купить эту книгу прямо сейчас 1. Вашему вниманию предлагается свыше трехсот замечательных кулинарных рецептов, снабженных цветными фотографиями. 2. Рецепты блюд, вошедшие в эту книгу, подойдут как к праздничному, так и к повседневному столу. 3. Продукты, входящие в состав блюд, есть в продаже круглый год и доступны по цене, 4. Предлагаемые рецепты заинтересуют как кулинаров-любителей, так и профессиональных поваров. 5. Исторические справки и любопытные факты сделают процесс приготовления блюд не только увлекательным, но и познавательным. 6. Предлагаемые блюда вкусны и полезны для здоровья. 7. Продуманный современный дизайн сделает эту книгу украшением вашей домашней библиотеки.', 'Володимир Рафеєнко', '75.90', '9789664816738.jpg', 'cooking', ''),
(84, 'Моя кухня', 'Знаменитый француз Жерар Депардье - прославленный актер, всеобщий любимец зрителей и артистический винодел. Личность многогранная, он открывает свой ресторан и пишет книгу "Моя кухня". Депардье не просто собрал рецепты любимых блюд, но и изложил свою гастрономическую философию. "Моя кухня" - это превосходная поваренная книга и в то же время путешествие в мир французской кухни, в котором читателя сопровождает увлеченный и, безусловно, знающий толк в еде спутник. Попробуйте! Он яркий, энергичный, щедрый и, главное, жизнелюб. В этой книге знаменитый актер и страстный винодел Жерар Депардье делится с читателями своими любимыми рецептами. Предпочтение он отдает национальным французским блюдам, которые нетрудно готовить. Залог успеха на кухне - натуральные продукты и желание порадовать друзей и близких, считает автор. Готовить нужно с любовью, говорит он, иначе блюдо никогда не получится по-настоящему вкусным. Депардье рассказывает о своей жизни, о том, как он увлекся виноделием и гастрономическим искусством, рассуждает о современных тенденциях в кулинарии и об утраченных традициях… И лишь затем отправляется вместе с нами на кухню.', 'Депардье Жерар', '99.99', '9785389005815.jpg', 'cooking', ''),
(85, 'Французская кухня. Кухни народов мира', '«Кухни народов мира» — это не просто 31 том с рецептами. Здесь масса информации о странах и их традициях — не только кулинарных. Известные повара и знаменитые люди из разных стран мира поделятся рецептами приготовления блюд своих народов и расскажут обо всех кухонных традициях своей страны.', 'Мария Распутина', '25.00', '9789662320633.jpg', 'cooking', ''),
(86, '1Q84. Тысяча невестьсот восемьдесят четыре. Книга 2. Июль - сентябрь', 'Впервые на русском! Абсолютно новый роман самого знаменитого автора современной японской прозы. Долгожданная новинка, имеющая огромный потенциал стать в 2011 бестселлером № 1 в России! Роман "1Q84" стал сенсацией культурной жизни Японии, и был признан "бестселлером года". К концу 2009 г. двухтомник вышел на первое место по количеству напечатанных экземпляров - 3,23 миллиона! Книга, написанная в лучших традициях стиля Мураками: реальность, иллюзия, научная фантастика, философия, познание человеческой души, протест против насилия и попрания свободы воли. Переводчик - один из лучших японистов Дмитрий Коваленин, открывший для российских читателей "Дэнс, дэнс, дэнс", "Охоту на овец" и "Послемрак". Наиболее ожидаемая новинка года, последний роман самого знаменитого автора современной японской прозы, главная литературная сенсация нового века, "магнум-опус прославленного мастера" и "обязательное чтение для любого, кто хочет разобраться в японской культуре наших дней", по выражению критиков. Действие книги происходит не столько в тысяча девятьсот восемьдесят четвертом году, сколько в тысяча невестьсот восемьдесят четвертом, в мире, где некоторые видят на небе две луны, где ключом к вечной любви служит Симфониетта Яначека, где полицейских после всколыхнувшей всю страну перестрелки с сектантами перевооружили автоматическими пистолетами взамен револьверов, где LittlePeople - Маленький Народец - выходят изо рта мертвой козы и плетут Воздушный Кокон.', 'Харуки Мураками', '99.80', '9785699509287.jpg', 'modern_literature', ''),
(87, 'Манускрипт, найденный в Акко', '14 июля 1099 года. Иерусалим замер в ожидании штурма крестоносцев. Жители города всех возрастов и разного вероисповедания пришли на площадь послушать загадочного старца Копта. Собравшиеся задают ему вопросы о своих тревогах, о врагах и битве. Сегодня, спустя столетия, ответы мудреца все так же верны, ведь он говорил о ценностях, над которыми время не властно. То, кем мы являемся, то, чего боимся и на что надеемся, основывается на нашем внутреннем знании и вере - и не зависит от внешних обстоятельств.', 'Пауло Коэльо', '92.50', '9785170776191.jpg', 'modern_literature', ''),
(88, 'Вангол', 'Эта честная и искренняя книга захватывает так, что невозможно отвлечься. Сердце замирает, и ты плачешь и радуешься вместе с ее героями, пронесшими с собой сквозь то тяжелое время веру, любовь, честь и преданность... В основе романа писателя Владимира Прасолова - реальные исторические события, происходившие в России в первой половине XX века. Беглый политзаключенный Иван Голышев, выхоженный эвенками-орочонами, заново рождается под именем Вангол. В тайге он находит семью, обретает мудрость древнего народа, но самое главное - находит себя и овладевает мастерством использовать скрытые человеческие возможности. Но в его дом приходит беда, и поток событий уносит его с собой. Сплетаются судьбы белого казачества и Красной армии, начальства и пленников лагерей, эвенков и жителей городов. И самые, казалось бы, далекие люди оказываются неразрывно связаны в этом водовороте страшного времени.\r\n', 'Владимир Прасолов', '98.90', '9785227042552.jpg', 'modern_literature', ''),
(89, 'Спаситель', 'Пытаясь заглушить тоску по Ракели, Харри Холе с головой погружается в работу. Теперь он совсем один: коллеги его не любят, старый начальник ушел, а новый превыше всего ставит дисциплину. В этом Харри не силен, зато он здорово расследует убийства. В Осло появился международный киллер, на благотворительном концерте застрелен солдат Армии спасения... Чтобы разобраться в невероятно запутанном преступлении и по-своему восстановить справедливость, Харри должен раскрыть немало зловещих тайн.', 'Ю Несбе', '96.20', '9785389055032.jpg', 'modern_literature', ''),
(90, 'Тайная книга Данте', 'Впервые на русском языке "Тайная книга Данте", роман Франческо Фьоретти, представителя нового поколения в итальянской литературе, одного из наследников Умберто Эко. Действительно ли Данте скончался от смертельной болезни, как полагали все в Равенне? Или же кто-то имел основания желать его смерти, желать, чтобы вместе с ним исчезла и тайна, принадлежавшая не ему? Мучимые сомнениями, дочь поэта Антония, бывший тамплиер по имени Бернар и врач Джованни, приехавший из Лукки, чтобы повидаться с поэтом, начинают двойное расследование. Они находят спрятанные в тайнике таблицы и пытаются расшифровать закодированное сообщение, оставленное Данте. Тем временем становится ясно, что далеко не все относились к поэту восторженно. Но кто именно настолько ненавидел его, что подослал к нему наемных убийц? И что связывает шифр последних песней "Божественной комедии" с тайной ковчега Завета?', 'Франческо Фьоретти', '92.50', '9785389036079.jpg', 'modern_literature', ''),
(91, 'Бэтман Аполло', 'Самая яркая новинка весны!!! Новый роман Виктора Пелевина "Бэтман Аполло"! Встречайте на полках книжного магазина "Читайка" новый роман Виктора Пелевина, пожалуй, самого загадочного, нестандартного и разнопланового писателя современной русской прозы. Чего же вы можете ожидать от новой книги «Бэтман Аполло»? Согласно анонсу на сайте издательства «Эксмо», речь в книге «про любовь, которая сильнее смерти», «про свет и тьму» и (что интересно) о российском протестном движении 2011—2013 годов. Да, информации немного, хотя так происходит всегда, когда речь заходит о романах этого писателя. Но, как впрочем, и всегда, книга будет достойной. Уж, кто-кто, а Пелевин относится к тому типу авторов, которые не привыкли разочаровывать своих читателей. Одно можно сказать с уверенностью: судя по информации, которая бороздит просторы интернета, читателей ожидает сиквел книги «Empire V». Отрывок из книги: «– Как ты еще молод, Рама. Вампир должен уметь не только прикидываться дураком – что, кстати, выходит у тебя замечательно. Он должен быть умнее любого из прислуживающих ему халдеев. Ты знаешь, откуда берется протест?»', 'Виктор Пелевин', '99.99', '9785699634460.jpg', 'modern_literature', ''),
(92, 'Шерлок Холмс', 'Английский врач и писатель сэр Артур Конан Дойл известен всему миру как непревзойденный мастер детективного жанра. В настоящем издании представлены наиболее известные рассказы о гениальном Шерлоке Холмсе (по словам писателя, "самом проницательном и энергичном из всех европейских сыщиков") и его верном спутнике докторе Ватсоне. Талантливо развивший традицию американского писателя Эдгара По, Артур Конан Дойл наряду с ним увенчан лаврами создателя детектива как жанра беллетристики. Читателю гарантировано огромное удовольствие - как от решения занимательных задач на логику, гибкость ума, так и просто от интересного чтения.', 'Артур Конан-Дойль', '45.00', '9785389050792.jpg', 'modern_literature', ''),
(93, 'Короткие гудки', 'Любовь побеждает не только расставания и смерть, но даже предательство, обиды и ненависть… Герои нового сборника Виктории Токаревой приходят к осознанию этого через неизбежные человеческие страдания, противоречивые повороты судьбы. Что-то неуловимое и всепрощающее вдруг оказывается сильнее страстей человека. И уже никто никого не судит, у каждого свои столкновения с собой и миром, свои поиски сквозь ошибки. "Пушкинское спокойствие" - так можно сказать о прозе Виктории Токаревой. Ее произведения утешают, помогают видеть жизнь как нечто неразгаданное.', 'Виктория Токарева', '90.00', '9785389044500.jpg', 'modern_literature', ''),
(94, 'Государь', 'Государь', 'Тим Филлипс', '48.30', '9789851510180.jpg', 'historical_novels', ''),
(95, 'Любовник смерти.', 'Памяти XIX столетия, когда литература была великой, вера в прогресс безграничной, а преступления совершались и раскрывались с изяществом и вкусом.\r\n', 'Борис Акунин', '15.89', '9789668959837.jpg', 'historical_novels', ''),
(96, 'Политические тайны Второй мировой', 'Вторая мировая война - историческое событие такого масштаба, что оказывает огромное влияние и на современную политику ведущих государств планеты. Стоит вспомнить хотя бы скандал вокруг Катыни, нескончаемые споры по поводу пакта Молотова - Риббентропа и прочие будоражащие умы вопросы, так и не получившие на сегодняшний день ответа. Несмотря на то что прошло уже более 70 лет со дня начала Второй мировой войны, многие политические события, сопровождавшие ее, так и остались тайной за семью печатями. Автор этой книги - политик, журналист, военачальник - попытался приподнять таинственный покров, скрывающий истину, и рассказать читателю подлинную политическую подоплеку самой кровавой войны в истории человечества.', 'Виктор Устинов', '83.90', '9785443800295.jpg', 'historical_novels', ''),
(97, 'Тайны Берлина. История. Легенды. Предания', 'Берлин был основан в начале XIII века на месте разрозненных славянских поселений. Его дальнейшая история на протяжении веков во многом похожа на судьбы других крупных городов Европы и в то же время она уникальна и вместе с тем трагична. Много тайн хранят дома, улицы, площади, мосты, сады и парки Берлина. Часто эти тайны связаны с выдающимися людьми и событиями, определившими ход мировой истории. О некоторых из них рассказывает книга М.Н. Кубеева.', 'Михаил Кубеев', '63.60', '9785953316231.jpg', 'historical_novels', ''),
(98, 'Ударные инструменты для чайников', 'Автор книги в простой и увлекательной форме рассказывает об искусстве игры на ударных инструментах. Вы узнаете, что такое ударная установка и как ее собрать, освоите технику игры палочками и руками, познакомитесь с основами полиритмии. Освоив простейшую технику игры на ударных, вы научитесь играть по схеме, мастерски делать заполнения и строить собственные солопартии. Научиться играть на барабанах не так уж сложно, и нужно для этого не так уж много — прочитать эту книгу и потратить некоторое количество денег, времени и сил. Книга окажется полезной как для начинающих музыкантов, так и для тех, кто уже имеет опыт игры на ударных инструментах.', 'Стронг Джефф', '99.99', '9785845908445.jpg', 'musical_literature', ''),
(99, 'Рок-гитара для чайников', 'Быть рок-гитаристом - что может быть круче? Ведь это и искусство, и страсть, и драйв... Это целая жизнь! Из рок-гитары можно извлечь мощный рев аккордов и пронзительные по выразительности мелодии - ни один другой музыкальный инструмент не обладает такой широкой звуковой палитрой. Независимо от того, каким направлением рока вы живете, книга "Рок-гитара для "чайников" поможет вам научиться выражать свои чувства, извлекая звуки из той электрогитары, которую вы держите в руках. Если вы новичок, то с ее помощью сможете быстро освоить базовую технику игры, не погружаясь в дебри музыкальной теории; если вы уже умеете играть на рок-гитаре, то наверняка эта книга поможет вам повысить свое исполнительское мастерство. В книге рассмотрены следующие темы: основные сведения о конструкции и принципах работы электрогитары и усилителей; выбор гитары и усилителя и уход за ними; постановка правой и левой руки и приемы исполнения; игра на рок-гитаре в разных стилях; риффы и их исполнение; краткая история рок-гитары; советы по приобретению необходимых аксессуаров - и многое другое. К книге прилагается компакт-диск с записями всех рассмотренных в ней примеров. Итак, если вас уже не устраивают песни у костра и вам хочется узнать, что такое настоящий драйв, эта книга поможет вам стать выдающимся гитаристом. К тому же это отличное дополнение к книге. Книга рассчитана на широкую читательскую аудиторию любого возраста.', 'Джон Чапел', '86.70', '9785845907677.jpg', 'musical_literature', ''),
(100, 'Иллюстрированная энциклопедия классической музыки', 'Музыка - загадочная и прекрасная форма искусства - понятна каждому человеку, независимо от того, является ли он обладателем абсолютного слуха и имеет ли музыкальное образование. В мире найдется совсем немного людей, неспособных реагировать на музыку. Музыка рождает в слушателе целый ряд эмоций, проникает в подсознание человека и надолго отпечатывается в памяти. Самая полная иллюстрированная энциклопедия классической музыки дает исчерпывающую информацию по истории музыкального искусства и творчеству гениальных музыкантов всех времен. Более 1000 высококачественных фотографий и иллюстраций, позволяющих в деталях составить представление обо всех элементах музыкальной культуры.', 'Макс Уэйд-Метью', '99.50', '9785699245673.jpg', 'musical_literature', ''),
(101, 'Уроки элементарной теории музыки', 'Настоящее учебное пособие основано на новой методике преподавания элементарной теории музыки, выработанной и апробированной непосредственно в педагогической практике автора. Нацеленное на поступенное освоение курса, пособие опирается на требования современной педагогики, связанные с развивающим обучением. Предназначается для учащихся начальных специализированных учебных заведений системы Министерства культуры и искусств Украины, школ и лицеев эстетического воспитания, музыкальных училищ, подготовительных отделений вокальных факультетов высших учебных заведений, изучающих двухгодичный курс элементарной теории музыки.\r\n', 'Мария Калашник', '20.70', '9789663126951.jpg', 'musical_literature', '');
